,p_process_name=>'Init'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_monitor            varchar2(100) := ''N'';',
'  l_SNAP_ASH           varchar2(100) := ''N'';',
