'    nvl(db_description,''N/A''),',
'    dump_description,',
'    tq_id,',
'    nvl(source_retention,-1),',
'    nvl(parsed_retention,-1),',
'    dbid,',
'    min_snap_id,',
'    max_snap_id,',
'    min_snap_dt,',
